mapper.insert:
SELECT COALESCE( ( SELECT SEQ FROM RECRUIT WHERE NAME = #{name} AND PHONE = #{phone} ),( SELECT COALESCE(TO_CHAR(MAX(TO_NUMBER(SEQ)+1)),'1') FROM RECRUIT) ) FROM DUAL
BEGIN
MERGE INTO RECRUIT R USING DUAL ON( R.SEQ = #{seq} ) WHEN MATCHED THEN UPDATE SET BIRTH = #{birth}, GENDER = #{gender}, PHONE = #{phone}, EMAIL = #{email}, ADDR = #{addr}, LOCATION = #{location}, WORK_TYPE = #{workType}, SUBMIT = 'N' WHEN NOT MATCHED THEN INSERT (SEQ, NAME, BIRTH, GENDER, PHONE, EMAIL, ADDR, LOCATION, WORK_TYPE, SUBMIT) VALUES (#{seq}, #{name}, #{birth}, #{gender}, #{phone}, #{email}, #{addr}, #{location}, #{workType}, 'N');
DELETE FROM EDUCATION WHERE SEQ = #{seq}; DELETE FROM CAREER WHERE SEQ = #{seq}; DELETE FROM CERTIFICATE WHERE SEQ = #{seq};
INSERT ALL INTO EDUCATION ( SEQ, EDU_SEQ, SCHOOL_NAME, DIVISION, START_PERIOD, END_PERIOD, MAJOR, GRADE, LOCATION )
SELECT #{seq} AS SEQ, #{education.eduSeq} AS EDU_SEQ, #{education.schoolName} AS SCHOOL_NAME, #{education.division} AS DIVISION, #{education.startPeriod} AS START_PERIOD, #{education.endPeriod} AS END_PERIOD, #{education.major} AS MAJOR, #{education.grade} AS GRADE, #{education.location} AS LOCATION FROM DUAL INSERT ALL INTO CAREER ( SEQ, CAR_SEQ, COMP_NAME, LOCATION, START_PERIOD, END_PERIOD, TASK )
SELECT #{seq} AS SEQ, #{career.carSeq} AS CAR_SEQ, #{career.compName} AS COMP_NAME, #{career.location} AS LOCATION, #{career.startPeriod} AS START_PERIOD, #{career.endPeriod} AS END_PERIOD, #{career.task} AS TASK FROM DUAL INSERT ALL INTO CERTIFICATE ( SEQ, CERT_SEQ, QUALIFI_NAME, ACQU_DATE, ORGANIZE_NAME )
SELECT #{seq} AS SEQ, #{certificate.certSeq} AS CERT_SEQ, #{certificate.qualifiName} AS QUALIFI_NAME, #{certificate.acquDate} AS ACQU_DATE, #{certificate.organizeName} AS ORGANIZE_NAME FROM DUAL END;
