mapper.update:
BEGIN UPDATE CLIENT_INFO SET IS_REQUESTED = 'Y' WHERE SEQ = #{seq}; UPDATE TRAVE_INFO SET REQUEST = 'M' WHERE SEQ = #{seq} AND TRAVE_SEQ IN
#{traveSeq}
END;

mapper.select:
SELECT C.SEQ, C.USER_NAME, C.USER_PHONE, C.TRAVE_CITY, C.PERIOD, C.EXPEND, C.TRANSPORT, C.EST_EXPEND, C.RENT_EXPEND, C.IS_REQUESTED, T.TRAVE_SEQ, T.SEQ, T.TRAVE_DAY, T.TRAVE_TIME, T.TRAVE_CITY, T.TRAVE_COUNTY, T.TRAVE_LOC, T.TRAVE_TRANS, T.TRANS_TIME, T.USE_TIME, T.USE_EXPEND, T.TRAVE_DETAIL, T.REQUEST, T.TRAVE_FARE FROM CLIENT_INFO C LEFT JOIN TRAVE_INFO T ON C.SEQ = T.SEQ WHERE
C.SEQ = #{seq}
C.USER_NAME = #{userName} AND C.USER_PHONE = #{userPhone}
ORDER BY T.TRAVE_SEQ ASC
