mapper.select:
SELECT BOARD_TYPE ,BOARD_NUM ,BOARD_TITLE ,BOARD_COMMENT ,TOTAL_CNT ,CREATE_TIME ,MODIFIED_TIME FROM ( SELECT BOARD_TYPE ,BOARD_NUM ,BOARD_TITLE ,BOARD_COMMENT ,ROW_NUMBER() OVER(ORDER BY BOARD_NUM DESC) AS NUMROW ,CREATE_TIME ,MODIFIED_TIME ,COUNT(*) OVER() AS TOTAL_CNT FROM BOARD WHERE BOARD_TYPE IN
#{item}
) WHERE NUMROW BETWEEN 1 AND 20 ORDER BY BOARD_NUM

mapper.update:
	UPDATE
		BOARD
	SET
        BOARD_TITLE 	= #{boardTitle}
        ,BOARD_COMMENT 	= #{boardComment}
        ,MODIFIER		= 'SYSTEM'
        ,MODIFIED_TIME	= TO_CHAR(SYSDATE)
	WHERE
		BOARD_NUM		= #{boardNum}
